mapper.select:
SELECT o.*, pa.name AS parentName, p.name AS projectName
FROM u_operation o 
LEFT JOIN sys_project p ON o.projectId = p.id
LEFT JOIN u_operation pa ON o.parentId = pa.id
WHERE o.status != %{CloseStatus.DELETE}

	AND o.id = #{id}


	AND o.projectId = #{projectId}


	AND o.url = #{url}

mapper.sql:
FROM u_operation o 
LEFT JOIN sys_project p ON o.projectId = p.id
LEFT JOIN u_operation pa ON o.parentId = pa.id
WHERE o.status != %{CloseStatus.DELETE}

	AND pa.name LIKE CONCAT('%',#{parentName},'%')


	AND p.name LIKE CONCAT('%',#{projectName},'%')


	AND o.name LIKE CONCAT('%',#{operationName},'%')


	AND o.url LIKE CONCAT('%',#{url},'%')


	AND o.isMenu = #{isMenu}


	AND o.status = #{status}

mapper.select:
SELECT * FROM u_operation o 
WHERE o.status = %{CloseStatus.OPEN}

	AND o.projectId = #{projectId}


	AND o.isMenu = #{isMenu}


	AND EXISTS ( SELECT * FROM u_role_operation ro WHERE ro.operationId = o.id 
		AND ro.roleId IN 

			#{roleId}

	 )

ORDER BY o.isMenu DESC, o.sort DESC, o.createTime DESC
